import React from 'react'
import { Col, Row } from 'react-bootstrap'

const Category = ({filterByCategory} , {allCategory}) => {
  
  const onFilter=(meal)=>{
    filterByCategory(meal);
  }
  return (
    <Row mb-5>
        <Col  sm="12" className='justify-content-center d-flex categ'>
          {
            allCategory.length>=1
          }
        <button onClick={()=>onFilter('الكل')}>الكل</button>
        <button onClick={()=>onFilter('فطار')}>فطار</button>
        <button onClick={()=>onFilter('غداء')}>غداء</button>
        <button onClick={()=>onFilter('عشاء')}>عشاء</button>
        </Col>
        
    </Row>
  )
}

export default Category
