    const allCategory = [...new Set]


  // filter By Category
  const filterByCategory =(meal)=>{
    if (meal === "الكل") {
     setItemsData(items);
    }else{
        const newArray =  items.filter((item)=>item.category === meal);
        setItemsData(newArray);
      }
  }
  return (
    <div className="">
      <NavBar />

